export namespace wizard {
    namespace model {
        const rules: {
            "#/mail/send": {
                set: string;
                value: (context: any) => void;
            }[];
        };
        namespace instance {
            namespace mail {
                const emailAddress: string;
                const message: string;
                const copy: string;
                const tags: any[];
            }
        }
    }
    const pages: ({
        label: string;
        fields: ({
            type: string;
            label: string;
            placeholder: string;
            required: boolean;
            bind: string;
            maxlength?: undefined;
            items?: undefined;
        } | {
            type: string;
            label: string;
            placeholder: string;
            bind: string;
            maxlength: number;
            required?: undefined;
            items?: undefined;
        } | {
            type: string;
            label: string;
            bind: string;
            items: string[];
            placeholder?: undefined;
            required?: undefined;
            maxlength?: undefined;
        })[];
    } | {
        label: string;
        fields: {
            type: string;
            label: string;
            bind: string;
            placeholder: string;
            autocomplete: {
                items: string[];
            };
        }[];
    } | {
        label: string;
        fields: ({
            type: string;
            label: string;
            innerText: string;
            bind?: undefined;
        } | {
            type: string;
            label: string;
            bind: string;
            innerText?: undefined;
        })[];
    })[];
}
