export default Router;
/**
 * SPA Router - replacement for Framework Routers (history and hash).
 */
declare class Router {
    constructor(options?: {});
    events: Events;
    options: {
        type: string;
    };
    /**
     * Start listening for route changes.
     * @returns {Router} reference to itself.
     */
    listen(): Router;
    routeHash: string[];
    _hashChanged(): void;
    _triggerPopState(e: any): void;
    _triggerRouteChange(path: any, url: any): void;
    _findRoute(url: any): string;
    _tryNav(href: any): boolean;
    _createUrl(href: any): URL;
    _onNavClick(e: any): void;
    /**
     * Makes the router navigate to the given route
     * @param {String} path
     */
    setRoute(path: string): void;
    get isHashRouter(): boolean;
}
import Events from "./Events";
