export default Context;
/**
 * XO Context - Gives access to contextual properties
 */
declare class Context {
    static _sheet: any;
    /**
     * @returns {CSSStyleSheet}
     */
    static get sharedStyles(): CSSStyleSheet;
    constructor(form: any);
    _form: any;
    _db: DataBinding;
    _mapper: PropertyMapper;
    /**
     * Returns a reference to the Form instance.
     * @returns {import('./Form.js') as Form}
     */
    get form(): typeof import("./Form.js");
    /**
     * Gets a reference to the DataBinding for the form.
     * @returns {DataBinding}
     */
    get data(): DataBinding;
    /**
     * @returns {PropertyMapper}
     */
    get mapper(): PropertyMapper;
}
import DataBinding from "./DataBinding";
import PropertyMapper from "./PropertyMapper";
