export default Form;
/**
 * XO Form Control (```<xo-form/>```)
 */
declare class Form extends Control {
    /**
     * Returns package version
     */
    static get version(): any;
    static get properties(): {
        page: {
            type: NumberConstructor;
            attribute: boolean;
        };
        schema: {
            type: ObjectConstructor;
        };
        src: {
            type: StringConstructor;
            attribute: boolean;
        };
        theme: {
            type: StringConstructor;
            attribute: boolean;
        };
        name: {
            type: StringConstructor;
            attribute: boolean;
        };
        bind: {
            type: StringConstructor;
        };
        type: {
            type: StringConstructor;
            attribute: boolean;
        };
        hidden: {
            type: BooleanConstructor;
        };
        disabled: {
            type: BooleanConstructor;
        };
        required: {
            type: BooleanConstructor;
        };
        focus: {
            type: BooleanConstructor;
        };
        label: {
            type: StringConstructor;
        };
        tooltip: {
            type: StringConstructor;
        };
        placeholder: {
            type: StringConstructor;
        };
        valid: {
            type: BooleanConstructor;
        };
        value: {
            type: ObjectConstructor;
        };
        prefix: {
            type: ObjectConstructor;
        };
        classes: {
            type: ArrayConstructor;
        };
    };
    elements: {};
    _url: URL;
    _context: Context;
    _page: number;
    /**
     * @param value {Number}
     */
    set page(arg: number);
    /**
     * @returns {Number}
     */
    get page(): number;
    /**
     * Sets the XO Form Schema to read.
     */
    set schema(arg: any);
    /**
     * Gets the XO Form Schema
     */
    get schema(): any;
    _schema: any;
    /**
     *
     * @param {HTMLElement} element
     */
    registerElement(element: HTMLElement): void;
    /**
     * Sets the URL to read an XO Form Schema from
     */
    set src(arg: any);
    /**
     * Returns the URL to read an XO Form Schema from
     */
    get src(): any;
    _src: any;
    readSchema(): Promise<boolean>;
    interpretSchema(schema: any): void;
    nav: Control;
    validation: Validation;
    emit(name: any, detail?: {}): void;
    get url(): URL;
    checkUrlState(): void;
    getSlotted(node: any): any[];
}
import Control from "./Control";
import Context from "./Context";
import Validation from "./Validation";
