export default Group;
/**
 * XO Group Control (```<xo-group/>```)
 */
declare class Group extends Control {
    static get properties(): {
        layout: {
            type: StringConstructor;
            attribute: boolean;
        };
        align: {
            type: StringConstructor;
            attribute: boolean;
        };
        ui: {
            type: StringConstructor;
            attribute: boolean;
        };
        fields: {
            type: ArrayConstructor;
        };
    };
    renderInput(): any;
    /**
     * Sets the declarative fields Array to append children with.
     * @param value {Array}
     */
    set fields(arg: any[]);
    /**
     * @returns {Array} - Array of schema fields that define the children to be appended.
     */
    get fields(): any[];
    _fields: any[];
    getGroupClasses(): string;
}
import Control from "./Control";
