export default Navigation;
/**
 * Navigation - Manages multi-step form navigation
 */
declare class Navigation extends Group {
    static get styles(): any[];
    static get properties(): {
        page: {
            type: NumberConstructor;
        };
        controls: {
            type: ArrayConstructor;
        };
    };
    beforeMap(): void;
    layout: string;
    _controls: any;
    set page(arg: any);
    get page(): any;
    updateUrl(): void;
    set controls(arg: any);
    get controls(): any;
    prev(): void;
    next(): void;
    updateState(dir: any): void;
    submit(): void;
}
import Group from "./Group";
