export default xo;
/**
 * XO - Namespace
 */
declare class xo {
    /**
     * XO Context
     */
    static get Context(): typeof Context;
    /**
     * XO Control (```<xo-control/>```) - both Base Control for XO, and wrapping Control for other HTML elements
     */
    static get Control(): typeof Control;
    /**
     * XO DataBinding - Manages dual-databinding within the form
     */
    static get DataBinding(): typeof DataBinding;
    /**
     * XO Form Control (```<xo-form/>```)
     */
    static get Form(): typeof Form;
    /**
     * XO Group Control (```<xo-group/>```)
     */
    static get Group(): typeof Group;
    /**
     * XO Property Mapper
     */
    static get PropertyMapper(): typeof PropertyMapper;
    /**
     * XO Page Control (```<xo-page/>```)
     */
    static get Page(): typeof Page;
    /**
     * XO Repeat Control (```<xo-repeat/>```) - Repeats underlying structure for all items in *.items* Array.
     */
    static get Repeat(): typeof Repeat;
    /**
     * Util Class - contains static helper methods
     */
    static get Util(): typeof Util;
    /**
     * Navigation - Manages multi-step form navigation
     */
    static get Navigation(): typeof Navigation;
    /**
     * Validation - manages form validation
     */
    static get Validation(): typeof Validation;
}
import Context from "./Context";
import Control from "./Control";
import DataBinding from "./DataBinding";
import Form from "./Form";
import Group from "./Group";
import PropertyMapper from "./PropertyMapper";
import Page from "./Page";
import Repeat from "./Repeat";
import Util from "./Util";
import Navigation from "./Navigation";
import Validation from "./Validation";
